mapper.insert:
		insert into Jumun(
		jumunNo
		, guestNo
		, tableNo
		, sushiNo
		, sushiCount)
		values(
		#{jumunNo}
		, #{guestNo}
		, #{tableNo}
		, #{sushiNo}
		, #{sushiCount}

		)

mapper.select:
		insert into Guest(
		guestNo
